mapper.select:
SELECT NOTICE_NUMBER, NOTICE_TITLE, NOTICE_CONTENT
FROM TBL_NOTICE
WHERE NOTICE_NUMBER = #{noticeNumber}

SELECT NOTICE_NUMBER, NOTICE_TITLE, NOTICE_CONTENT
FROM
(
SELECT
ROWNUM RN, NOTICE_NUMBER, NOTICE_TITLE, NOTICE_CONTENT
FROM TBL_NOTICE
WHERE

        ROWNUM <= #{pageNum} * #{amount}
    )
    WHERE RN > (#{pageNum} - 1) * #{amount}
