mapper.select:
SELECT ORDER_NUMBER, USER_NUMBER, ORDER_DATE, ORDER_PROGRESS, ORDER_ALERT, ORDER_REQUEST_OPTION, ORDER_REQUEST_MESSAGE, ORDER_TOTAL_QUANTITY, ORDER_TOTAL_PRICE
FROM (
    SELECT * FROM TBL_ORDER
    WHERE USER_NUMBER = #{userNumber}
    ORDER BY ORDER_DATE DESC
    )
WHERE ROWNUM = 1;

mapper.update:
UPDATE TBL_ORDER
SET ORDER_TOTAL_QUANTITY = #{orderTotalQuantity}, ORDER_TOTAL_PRICE = #{orderTotalPrice}
WHERE ORDER_NUMBER = #{orderNumber}
