mapper.select:
SELECT USER_NUMBER, USER_EMAIL, USER_PW, USER_NAME, USER_PHONENUM, USER_ADDRESS, USER_ADDRESS_DETAIL, USER_ENTRANCE_TYPE, USER_ENTRANCE_PW, USER_ENTRANCE_MESSAGE, USER_SERVICE_TYPE, USER_LAUNDERETTE_TYPE, USER_CARDNUM
FROM TBL_USER
WHERE USER_NUMBER = #{userNumber}

mapper.update:
UPDATE TBL_USER
SET USER_EMAIL = #{userEmail}, USER_PW = #{userPw}, USER_NAME = #{userName}, USER_PHONENUM = #{userPhonenum}, USER_ADDRESS = #{userAddress},  USER_ADDRESS_DETAIL = #{userAddressDetail}, USER_ENTRANCE_TYPE = #{userEntranceType}, USER_ENTRANCE_PW = #{userEntrancePw}, USER_ENTRANCE_MESSAGE = #{userEntranceMessage}, USER_SERVICE_TYPE = #{userServiceType}, USER_CARDNUM = #{userCardNum}
WHERE USER_NUMBER=#{userNumber}

UPDATE TBL_USER
USER_SERVICE_TYPE = #{userServiceType}, USER_LAUNDERETTE_TYPE = #{userLaunderetteType},
WHERE USER_NUMBER=#{userNumber}

UPDATE TBL_USER
USER_ADDRESS = #{userAddress}, USER_ADDRESS_DETAIL = #{userAddressDetail}, USER_ENTRANCE_TYPE = #{userEntranceType}, USER_ENTRANCE_PW = #{userEntrancePw}, USER_ENTRANCE_MESSAGE = #{userEntranceMessage},
WHERE USER_NUMBER = #{userNumber}
